import React from "react";
import { motion } from "framer-motion";

const About = () => {
  return (
    <div className="about-page">
      {/* ترويسة الصفحة */}
      <section className="about-hero">
        <motion.h1
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
        >
          من نحن
        </motion.h1>
        <motion.p
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.5, duration: 1 }}
        >
          نحن شركة سيارات متخصصة في تقديم أفضل السيارات الحديثة والمستعملة بجودة عالية وخدمة عملاء متميزة.
        </motion.p>
      </section>

      {/* احصائيات الشركة */}
      <section className="about-stats container">
        <motion.div className="stat" whileInView={{ scale: 1.1 }} initial={{ scale: 0 }} transition={{ duration: 0.5 }}>
          <h3>500+</h3>
          <p>سيارة مباعة</p>
        </motion.div>
        <motion.div className="stat" whileInView={{ scale: 1.1 }} initial={{ scale: 0 }} transition={{ duration: 0.5, delay: 0.2 }}>
          <h3>400+</h3>
          <p>عملاء سعداء</p>
        </motion.div>
        <motion.div className="stat" whileInView={{ scale: 1.1 }} initial={{ scale: 0 }} transition={{ duration: 0.5, delay: 0.4 }}>
          <h3>10+</h3>
          <p>سنوات خبرة</p>
        </motion.div>
      </section>

      {/* خط الزمن Timeline */}
      <section className="timeline-section container">
        <h2>تاريخنا</h2>
        <div className="timeline">
          <motion.div className="timeline-item" whileInView={{ x: 0, opacity: 1 }} initial={{ x: -100, opacity: 0 }} transition={{ duration: 0.5 }}>
            <span>2015</span>
            <p>تأسيس الشركة وافتتاح أول معرض سيارات.</p>
          </motion.div>
          <motion.div className="timeline-item" whileInView={{ x: 0, opacity: 1 }} initial={{ x: 100, opacity: 0 }} transition={{ duration: 0.5, delay: 0.2 }}>
            <span>2018</span>
            <p>توسيع الفروع وتقديم خدمات صيانة معتمدة.</p>
          </motion.div>
          <motion.div className="timeline-item" whileInView={{ x: 0, opacity: 1 }} initial={{ x: -100, opacity: 0 }} transition={{ duration: 0.5, delay: 0.4 }}>
            <span>2022</span>
            <p>تحقيق 500+ سيارة مباعة وزيادة قاعدة العملاء.</p>
          </motion.div>
        </div>
      </section>
    </div>
  );
};

export default About;
