import React, { useState } from "react";
import "./hero.css";
import Api from "./Home/Api";
function Hero_1() {
  let [gender, setGender] = useState("");
  let [mode, setModel] = useState("");
  let sendModel = (e) => {
    setModel(e.target.value);
  };
  let sendgender = (e) => {
    setGender(e.target.value);
  };
  return (
    <div className="">
      <section className="hero m-5">
        <div className="hero-overlay" />
        <div className="hero-content container">
          <h1 className="display-3 fw-medium ">ابحث عن سيارتك المثالية</h1>
          <p className="lead">
            <span className="fw-bolder gradient-text">Gana Trading </span> أفضل
            العروض معنا
          </p>
          <form
            className="row g-2 justify-content-center"
            onSubmit={(e) => {
              e.preventDefault();
              alert(`Gender: ${gender}, Mode: ${mode}`);
            }}
          >
            <div className="col-md-4">
              <input
                type="text"
                // onChange={(e) => setGender(e.target.value)}
                onChange={sendgender}
                className="form-control"
                placeholder="البحث عن الماركة"
              />
            </div>
            <div className="col-md-4">
              <input
                type="number"
                onChange={sendModel}
                className="form-control"
                placeholder="السنة"
              />
            </div>
            <div className="col-md-2">
              <button type="submit" className="btn btn-primary w-100">
                بحث
              </button>
            </div>
          </form>
        </div>
      </section>
      <Api></Api>
    </div>
  );
}

export default Hero_1;

