import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";

const ProductDetails = () => {
  const { id } = useParams();
  const [car, setCar] = useState(null);
  const [similarCars, setSimilarCars] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    fetchCarDetails();
  }, [id]);

  const fetchCarDetails = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/cars/${id}`);
      if (response.ok) {
        const data = await response.json();
        console.log("بيانات السيارة المسترجعة:", data); // للتأكد من البيانات

        // تحويل مسارات الصور إلى مسارات مطلقة
        const carWithFullImageUrls = {
          ...data.car,
          images: data.car.images.map((img) => ({
            ...img,
            image_url: `http://localhost:5000${img.image_url}`,
          })),
        };

        setCar(carWithFullImageUrls);
        setSimilarCars(data.similarCars);
      } else {
        setError("فشل في جلب تفاصيل السيارة");
      }
    } catch (err) {
      console.error("خطأ في جلب تفاصيل السيارة:", err);
      setError("حدث خطأ أثناء جلب تفاصيل السيارة");
    } finally {
      setLoading(false);
    }
  };

  const nextImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === car.images.length - 1 ? 0 : prevIndex + 1
    );
  };

  const prevImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? car.images.length - 1 : prevIndex - 1
    );
  };

  if (loading) return <div className="loading">جاري التحميل...</div>;
  if (error) return <div className="error">{error}</div>;
  if (!car) return <div className="error">السيارة غير موجودة</div>;

  return (
    <div className="product-details">
      <div className="car-images">
        <div className="main-image">
          {car.images && car.images.length > 0 ? (
            <>
              <img
                src={car.images[currentImageIndex].image_url}
                alt={car.name}
                onError={(e) => {
                  console.error(
                    "خطأ في تحميل الصورة:",
                    car.images[currentImageIndex].image_url
                  );
                  e.target.src = "https://via.placeholder.com/600x400";
                }}
              />
              {car.images.length > 1 && (
                <>
                  <button className="prev-btn" onClick={prevImage}>
                    &lt;
                  </button>
                  <button className="next-btn" onClick={nextImage}>
                    &gt;
                  </button>
                </>
              )}
            </>
          ) : (
            <img src="https://via.placeholder.com/600x400" alt="لا توجد صور" />
          )}
        </div>
        <div className="thumbnails">
          {car.images &&
            car.images.map((image, index) => (
              <img
                key={index}
                src={image.image_url}
                alt={`${car.name} ${index + 1}`}
                className={index === currentImageIndex ? "active" : ""}
                onClick={() => setCurrentImageIndex(index)}
                onError={(e) => {
                  console.error(
                    "خطأ في تحميل الصورة المصغرة:",
                    image.image_url
                  );
                  e.target.src = "https://via.placeholder.com/100x100";
                }}
              />
            ))}
        </div>
      </div>

      <div className="car-details">
        <h1>{car.name}</h1>
        <p className="price">{car.price} جنيه</p>
        <p className="description">{car.description}</p>

        <div className="features">
          <h2>المميزات</h2>
          <ul>
            {car.features &&
              car.features.map((feature, index) => (
                <li key={index}>{feature}</li>
              ))}
          </ul>
        </div>
      </div>

      {similarCars.length > 0 && (
        <div className="similar-cars">
          <h2>سيارات مشابهة</h2>
          <div className="similar-cars-list">
            {similarCars.map((similarCar) => (
              <Link
                key={similarCar.id}
                to={`/product/${similarCar.id}`}
                className="similar-car"
              >
                <img
                  src={
                    similarCar.images && similarCar.images[0]
                      ? similarCar.images[0].image_url
                      : "https://via.placeholder.com/200x150"
                  }
                  alt={similarCar.name}
                  onError={(e) => {
                    console.error(
                      "خطأ في تحميل صورة السيارة المشابهة:",
                      similarCar.images[0]?.image_url
                    );
                    e.target.src = "https://via.placeholder.com/200x150";
                  }}
                />
                <h3>{similarCar.name}</h3>
                <p>{similarCar.price} جنيه</p>
              </Link>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default ProductDetails;
