import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

const Api = ({ brand, model }) => {
  const [cars, setCars] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchCars();
  }, [brand, model]);

  const fetchCars = async () => {
    setLoading(true);
    setError("");
    try {
      const params = new URLSearchParams();
      if (brand) params.append("brand", brand);
      if (model) params.append("model", model);
      const url = `http://localhost:5000/api/cars${params.toString() ? `?${params}` : ""}`;
      const response = await fetch(url);
      if (!response.ok) throw new Error();
      const data = await response.json();
      setCars(data);
    } catch {
      setError("فشل في جلب بيانات السيارات");
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div className="loading">جاري التحميل...</div>;
  if (error) return <div className="error">{error}</div>;
  if (cars.length === 0) return <div className="text-center">لا توجد نتائج</div>;

  return (
    <div className="shop-container">
      <h1>المتجر</h1>
      <div className="cars-grid">
        {cars.map((car) => (
          <div key={car.id} className="car-card">
            <Link to={`/shop/${car.id}`}>
              <img
                src={car.images?.[0]?.image_url || "https://via.placeholder.com/150"}
                alt={car.name}
                onError={(e) => (e.target.src = "https://via.placeholder.com/150")}
              />
              <h3>{car.name}</h3>
              <p>السعر: {car.price} جنيه</p>
              <button className="view-btn">عرض التفاصيل</button>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Api;
