import React, { useState } from "react";
import "./hero.css";
import Api from "./Home/Api";

function Hero_1() {
  // إعادة تسمية المتغيرات لتدل على العلامة التجارية والموديل
  const [brand, setBrand] = useState("");
  const [model, setModel] = useState("");

  const handleBrandChange = (e) => {
    setBrand(e.target.value);
  };
  const handleModelChange = (e) => {
    setModel(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // هنا لا نحتاج alert، الاستعلام يَتم داخل Api بناءً على props
  };

  return (
    <div className="pt-2">
      <section className="hero mb-5">
        <div className="hero-overlay" />
        <div className="hero-content container">
          <h1 className="display-3 fw-medium">ابحث عن سيارتك المثالية</h1>
          <p className="lead">
            <span className="fw-bolder gradient-text">Gana Trading</span> أفضل
            العروض معنا
          </p>
          <form
            className="row g-2 justify-content-center"
            onSubmit={handleSubmit}
          >
            <div className="col-md-4">
              <input
                type="text"
                onChange={handleBrandChange}
                value={brand}
                className="form-control"
                placeholder="البحث عن الماركة"
              />
            </div>
            <div className="col-md-4">
              <input
                type="text"
                onChange={handleModelChange}
                value={model}
                className="form-control"
                placeholder="البحث عن الموديل"
              />
            </div>
            <div className="col-md-2">
              <button type="submit" className="btn btn-primary w-100">
                بحث
              </button>
            </div>
          </form>
        </div>
      </section>
      {/* تمرير brand و model كـ props إلى Api */}
      <Api brand={brand} model={model} />
    </div>
  );
}

export default Hero_1;
