import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";

function ProductDetails() {
  const { id } = useParams();
  const [car, setCar] = useState(null);
  const [similarCars, setSimilarCars] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    const fetchCarDetails = async () => {
      try {
        const response = await axios.get(
          `http://localhost:5000/api/cars/${id}`
        );
        setCar(response.data.car);
        setSimilarCars(response.data.similarCars);
        setLoading(false);
      } catch (error) {
        console.error("حدث خطأ في جلب تفاصيل السيارة:", error);
        setLoading(false);
      }
    };

    fetchCarDetails();
  }, [id]);

  const nextImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === car.images.length - 1 ? 0 : prevIndex + 1
    );
  };

  const prevImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? car.images.length - 1 : prevIndex - 1
    );
  };

  if (loading) {
    return <div className="loading">جاري التحميل...</div>;
  }

  if (!car) {
    return <div className="error">السيارة غير موجودة</div>;
  }

  return (
    <div className="product-details">
      <div className="car-details">
        <div className="car-images">
          <div className="main-image">
            <img src={car.images[currentImageIndex].image_url} alt={car.name} />
            {car.images.length > 1 && (
              <>
                <button className="prev-btn" onClick={prevImage}>
                  ←
                </button>
                <button className="next-btn" onClick={nextImage}>
                  →
                </button>
              </>
            )}
          </div>
          <div className="thumbnails">
            {car.images.map((image, index) => (
              <img
                key={image.id}
                src={image.image_url}
                alt={`${car.name} - صورة ${index + 1}`}
                className={index === currentImageIndex ? "active" : ""}
                onClick={() => setCurrentImageIndex(index)}
              />
            ))}
          </div>
        </div>

        <div className="car-info">
          <h1>{car.name}</h1>
          <p>الماركة: {car.brand}</p>
          <p>الموديل: {car.model}</p>
          <p>السنة: {car.year}</p>
          <p>السعر: {car.price} ريال</p>
          <p>الوصف: {car.description}</p>
          <div className="features">
            <h2>المميزات:</h2>
            <ul>
              {Object.entries(JSON.parse(car.features)).map(([key, value]) => (
                <li key={key}>
                  {key}: {value}
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>

      <div className="similar-cars">
        <h2>سيارات مشابهة</h2>
        <div className="similar-cars-grid">
          {similarCars.map((similarCar) => (
            <div key={similarCar.id} className="similar-car-card">
              <img
                src={similarCar.images[0]?.image_url}
                alt={similarCar.name}
              />
              <h3>{similarCar.name}</h3>
              <p>
                {similarCar.brand} - {similarCar.model}
              </p>
              <p>السعر: {similarCar.price} ريال</p>
              <Link to={`/shop/${similarCar.id}`} className="details-btn">
                عرض التفاصيل
              </Link>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default ProductDetails;
