import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const AdminPanel = () => {
  const [cars, setCars] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [showForm, setShowForm] = useState(false);
  const [editingCar, setEditingCar] = useState(null);
  const navigate = useNavigate();

  // بيانات السيارة الجديدة
  const [carData, setCarData] = useState({
    name: "",
    price: "",
    description: "",
    features: [""],
    images: [],
    brand: "",
    model: "",
    year: ""
  });

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
      return;
    }

    fetchCars();
  }, [navigate]);

  const fetchCars = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/cars", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });

      if (response.ok) {
        const data = await response.json();
        setCars(data);
      } else {
        setError("فشل في جلب بيانات السيارات");
      }
    } catch (err) {
      setError("حدث خطأ أثناء جلب بيانات السيارات");
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCarData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleFeatureChange = (index, value) => {
    const newFeatures = [...carData.features];
    newFeatures[index] = value;
    setCarData((prev) => ({
      ...prev,
      features: newFeatures,
    }));
  };

  const handleImageUpload = async (e) => {
    const files = Array.from(e.target.files);
    const formData = new FormData();

    files.forEach((file) => {
      formData.append("images", file);
    });

    try {
      const response = await fetch("http://localhost:5000/api/upload", {
        method: "POST",
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: formData,
      });

      if (response.ok) {
        const data = await response.json();
        console.log("الصور المرفوعة:", data.urls);
        setCarData((prev) => ({
          ...prev,
          images: [...prev.images, ...data.urls],
        }));
      } else {
        const errorData = await response.json();
        setError(errorData.message || "فشل في رفع الصور");
      }
    } catch (err) {
      console.error("خطأ في رفع الصور:", err);
      setError("حدث خطأ أثناء رفع الصور: " + err.message);
    }
  };

  const addFeatureField = () => {
    setCarData((prev) => ({
      ...prev,
      features: [...prev.features, ""],
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const url = editingCar
        ? `http://localhost:5000/api/cars/${editingCar.id}`
        : "http://localhost:5000/api/cars";

      const method = editingCar ? "PUT" : "POST";

      // تحضير البيانات قبل الإرسال
      const carDataToSend = {
        name: carData.name,
        price: parseFloat(carData.price),
        description: carData.description,
        features: carData.features.filter(feature => feature.trim() !== ""),
        images: carData.images,
        brand: carData.brand || "",
        model: carData.model || "",
        year: carData.year || 2024
      };

      console.log("بيانات السيارة المرسلة:", carDataToSend); // للتأكد من البيانات

      const response = await fetch(url, {
        method,
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify(carDataToSend),
      });

      if (response.ok) {
        const result = await response.json();
        console.log("استجابة الخادم:", result); // للتأكد من الاستجابة
        fetchCars();
        setShowForm(false);
        setEditingCar(null);
        setCarData({
          name: "",
          price: "",
          description: "",
          features: [""],
          images: [],
          brand: "",
          model: "",
          year: ""
        });
      } else {
        const errorData = await response.json();
        console.error("خطأ في الاستجابة:", errorData);
        setError(errorData.message || "فشل في حفظ البيانات");
      }
    } catch (err) {
      console.error("خطأ في إرسال البيانات:", err);
      setError("حدث خطأ أثناء حفظ البيانات: " + err.message);
    }
  };

  const handleEdit = (car) => {
    setEditingCar(car);
    setCarData({
      name: car.name,
      price: car.price,
      description: car.description,
      features: car.features,
      images: car.images,
      brand: car.brand || "",
      model: car.model || "",
      year: car.year || 2024
    });
    setShowForm(true);
  };

  const handleDelete = async (carId) => {
    if (window.confirm("هل أنت متأكد من حذف هذه السيارة؟")) {
      try {
        const response = await fetch(
          `http://localhost:5000/api/cars/${carId}`,
          {
            method: "DELETE",
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          }
        );

        if (response.ok) {
          fetchCars();
        } else {
          setError("فشل في حذف السيارة");
        }
      } catch (err) {
        setError("حدث خطأ أثناء حذف السيارة");
      }
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  if (loading) return <div className="loading">جاري التحميل...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="admin-panel">
      <div className="container">
        <div className="header">
          <h1>لوحة التحكم</h1>
          <button onClick={handleLogout}>تسجيل الخروج</button>
        </div>

        <button
          onClick={() => {
            setShowForm(true);
            setEditingCar(null);
            setCarData({
              name: "",
              price: "",
              description: "",
              features: [""],
              images: [],
              brand: "",
              model: "",
              year: ""
            });
          }}
        >
          إضافة سيارة جديدة
        </button>

        {showForm && (
          <form className="car-form" onSubmit={handleSubmit}>
            <h2>{editingCar ? "تعديل سيارة" : "إضافة سيارة جديدة"}</h2>

            <div className="form-group">
              <label>اسم السيارة</label>
              <input
                type="text"
                name="name"
                value={carData.name}
                onChange={handleInputChange}
                required
              />
            </div>

            <div className="form-group">
              <label>السعر</label>
              <input
                type="number"
                name="price"
                value={carData.price}
                onChange={handleInputChange}
                required
              />
            </div>

            <div className="form-group">
              <label>الوصف</label>
              <textarea
                name="description"
                value={carData.description}
                onChange={handleInputChange}
                required
              />
            </div>

            <div className="form-group">
              <label>المميزات</label>
              <div className="features-inputs">
                {carData.features.map((feature, index) => (
                  <div key={index} className="feature-input">
                    <input
                      type="text"
                      value={feature}
                      onChange={(e) =>
                        handleFeatureChange(index, e.target.value)
                      }
                      required
                    />
                    {index === carData.features.length - 1 && (
                      <button type="button" onClick={addFeatureField}>
                        +
                      </button>
                    )}
                  </div>
                ))}
              </div>
            </div>

            <div className="form-group">
              <label>الصور</label>
              <input
                type="file"
                multiple
                accept="image/*"
                onChange={handleImageUpload}
              />
              <div className="uploaded-images">
                {carData.images.map((image, index) => (
                  <div key={index} className="image-preview">
                    <img
                      src={image}
                      alt={`صورة ${index + 1}`}
                      onError={(e) => {
                        console.error("خطأ في تحميل الصورة:", image);
                        e.target.src = "https://via.placeholder.com/150";
                      }}
                    />
                  </div>
                ))}
              </div>
            </div>

            <div className="form-actions">
              <button type="submit">حفظ</button>
              <button type="button" onClick={() => setShowForm(false)}>
                إلغاء
              </button>
            </div>
          </form>
        )}

        <div className="cars-list">
          <h2>السيارات</h2>
          <table>
            <thead>
              <tr>
                <th>الصورة</th>
                <th>الاسم</th>
                <th>السعر</th>
                <th>الإجراءات</th>
              </tr>
            </thead>
            <tbody>
              {cars.map((car) => (
                <tr key={car.id}>
                  <td>
                    <div className="car-images-preview">
                      {car.images &&
                        car.images.map((image, index) => (
                          <img
                            key={index}
                            src={image}
                            alt={`${car.name} ${index + 1}`}
                          />
                        ))}
                    </div>
                  </td>
                  <td>{car.name}</td>
                  <td>{car.price} جنيه</td>
                  <td>
                    <button onClick={() => handleEdit(car)}>تعديل</button>
                    <button onClick={() => handleDelete(car.id)}>حذف</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
